import UIKit

protocol LoadedCityExplainPresentationLogic {
  	func presentData(response: LoadedCity.Data.Response)
  	func returnBackToPreviousViewController()
}

class LoadedCityExplainPresenter: LoadedCityExplainPresentationLogic {
  	weak var viewController: LoadedCityExplainDisplayLogic?
	weak var router: LoadedCityExplainRouter?

  	// MARK: LoadedCityExplainPresentationLogic

  	func presentData(response: LoadedCity.Data.Response) {
    	let viewModel = LoadedCity.Data.ViewModel(cityName: response.cityName)
    	viewController?.updateData(viewModel: viewModel)
  	}

  	func returnBackToPreviousViewController() {
  		router?.dismissCurrentViewController()
  	}
}
