import UIKit

protocol IntroNotifSetUpRoutingLogic {
  	//func routeToSomewhere(segue: UIStoryboardSegue?)
  	func presentNotifSetUpScene(response: IntroNotifSetUp.PushNotif.Response)
}

protocol IntroNotifSetUpDataPassing {
  	var dataStore: IntroNotifSetUpDataStore? { get }
}

class IntroNotifSetUpRouter: NSObject, IntroNotifSetUpRoutingLogic, IntroNotifSetUpDataPassing {
  	weak var viewController: IntroNotifSetUpViewController?
  	var dataStore: IntroNotifSetUpDataStore?
	
	
	func presentNotifSetUpScene(response: IntroNotifSetUp.PushNotif.Response) {
		let storyboard = UIStoryboard(name: "Main", bundle: nil)
  		let destinationVC = storyboard.instantiateViewController(withIdentifier: "pushNotifSetupViewController") as! PushNotifSetupViewController
  		destinationVC.router?.previousViewController = viewController!
  		destinationVC.providesPresentationContextTransitionStyle = true
		destinationVC.definesPresentationContext = true
		destinationVC.modalPresentationStyle=UIModalPresentationStyle.overCurrentContext
		viewController!.present(destinationVC, animated: true) { }
	}
}
